/* Grid */
.gdpr {
  position: fixed;
  left: 0;
  bottom: 0;
  width: 100%;
  pointer-events: none;
  z-index: 500;
}
.gdpr .drawer {
  display: inline-block;
  pointer-events: all;
  background-color: rgba(35, 31, 32, 0.8);
  color: white;
  padding: 8px 24px;
  border-top-left-radius: 6px;
  border-top-right-radius: 6px;
}
.gdpr .drawer span {
  font-size: 0.8rem;
  margin-right: 16px;
}
.gdpr.accepted {
  display: none;
}
@media only screen and (min-width: 640px) {
  .gdpr .drawer button {
    height: 40px;
    padding: 0 20px;
